import { regions } from './regions/data';

export default async function sitemap() {
  const baseUrl = 'https://svoedelo.ru';
  const staticRoutes = [
    '', '/autsource-buhgalterii', '/ip', '/ooo',
    '/sdacha-otchetnosti', '/vosstanovlenie-ucheta',
